import { isEditable } from '@testing-library/user-event/dist/utils';
import React from 'react'
import { useState } from 'react';

const Skills = () => {

    const [isClicked, setIsClicked] = useState(true)
    const [langClass, setLangClass] = useState("lang")

    const openSkill = (element) => {
        if (isClicked){
            setLangClass("lang-show")
            console.log("skill should pop")
        }
        else{
            setLangClass("lang")
            console.log("skills should hide")
        }
        setIsClicked(!isClicked)
    }

    return (
        <>  
            <div className='white-bg-1'>
                <section data-aos="fade-up" className="skills-container">
                    <div className="titles">
                        <h1>Skills</h1>
                    </div>
                    <div className='skills'>
                        <div className='programming-languages'>
                            <a onClick={openSkill}><img className={langClass} src='https://picsum.photos/200'></img></a>
                            <a onClick={openSkill}><img className={langClass} src='https://picsum.photos/200'></img></a>
                            <a onClick={openSkill}><img className={langClass} src='https://picsum.photos/200'></img></a>
                            <a onClick={openSkill}><img className={langClass} src='https://picsum.photos/200'></img></a>
                        </div>
                    </div>
                </section>
            </div>
        </>
    );
}

export default Skills;
