import React from 'react'
import { useState } from 'react';
import SkillItem from './SkillItem';
import SkillPopup from './SkillPopup';
import cppLogo from "./../assets/skills-assets/cpp-logo.png"
import pythonLogo from "./../assets/skills-assets/python-logo.png"
import swiftLogo from "./../assets/skills-assets/swift-logo.png"
import webLogo from "./../assets/skills-assets/web-logo.png"

const Popup = ({ animal }) => {
  const [showPopup, setShowPopup] = useState(false);

  return (
    <div>
      <img src={animal.image} onClick={() => setShowPopup(true)} />
      {showPopup && (
        <div className="popup">
          <h2>{animal.name}</h2>
          <p>{animal.description}</p>
          <button onClick={() => setShowPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
};

const Animal = ({ name, image, description }) => {
  return <Popup animal={{ name, image, description }} />;
};

const App = () => {
  return (
    <div>
      <Animal
        name="Lion"
        image="https://image.com/lion.jpg"
        description="The lion is a species of big cat that is native to Africa."
      />
      <Animal
        name="Elephant"
        image="https://image.com/elephant.jpg"
        description="The elephant is the largest land animal on Earth and is native to Africa and Asia."
      />
      <Animal
        name="Giraffe"
        image="https://image.com/giraffe.jpg"
        description="The giraffe is a tall species of mammal that is native to Africa."
      />
    </div>
  );
};

export default App;
