import { isEditable } from '@testing-library/user-event/dist/utils';
import React from 'react'
import { useState } from 'react';

const Skills = () => {

    const [isClicked, setIsClicked] = useState(true)
    const [langClass, setLangClass] = useState("lang")
    const [hidden, setHidden] = useState("hidden")

    return (
        <>  
            <div className='white-bg-1'>
                <section data-aos="fade-up" className="skills-container">
                    <div className="titles">
                        <h1>Skills</h1>
                    </div>
                    <div className='skills'>
                        <div className='programming-languages'>
                            <img src='https://picsum.photos/200'></img>
                        </div>
                    </div>
                </section>
            </div>
        </>
    );
}



const SkillItem = (props) => {

    const [skillPopup, setSkillPopup] = useState(false)

    return(
        <div className="skill-popup" onClick={() => setSkillPopup(!skillPopup)}>
            <img src={props.image} alt={props.name} />
            {skillPopup && <div className="animal-name">{props.name}</div>}
        </div>
    )
}


export default Skills;
