import React from 'react'
import { useState } from 'react';
import SkillItem from './SkillItem';
import SkillPopup from './SkillPopup';

const Skills = () => {

    const [popupVisible, setPopupVisible] = useState("visible")
    const [popupClass, setPopupClass] = useState("")

    const popup = (skill) => {

        const skillDictionary = {
            "c++":{
                name:"C/C++",
                description:"Used to make terminal application and implement advanced algorithms"
            },
            "python": {
                name:"Python",
                description:"Used to make webscraping application and AI and ML projects"
            },
            "swift": {
                name:"Swift",
                description:"Used to make iOS application"
            },
            "web": {
                name:"HTML, CSS & JavaScript",
                description:"Used to make Fully Functional Web Applications."
            }
        }

        if (popupVisible === "visible"){
            setPopupVisible("hidden")
        }
        else{
            setPopupVisible("visible")
        }
        console.log(popupVisible)
        setPopupClass(skill)
        console.log(popupClass)

        return skillDictionary[skill]
    }

    return (
        <div className='white-bg-1'>
            <section data-aos="fade-up" className="skills-container">
                <div className="titles">
                    <h1>Skills</h1>
                </div>
                <div className='skills'>
                <p>Click on the skills below for more details!</p>
                    <div className='programming-languages'>
                        <a onClick= {() => {popup("c++")}}><SkillItem img="https://picsum.photos/200"/></a>
                        <a onClick= {() => {popup("python")}}><SkillItem img="https://picsum.photos/200"/></a>
                    </div>
                    <SkillPopup isVisible={popupVisible} skill={popupClass}/>
                </div>
            </section>
        </div>
    );
}

export default Skills;
