import React from 'react'
import { useState } from 'react';
import SkillItem from './SkillItem';
import SkillPopup from './SkillPopup';
import cppLogo from "./../assets/skills-assets/cpp-logo.png"

const Skills = () => {

    const [popupVisible, setPopupVisible] = useState("visible")
    const [popupClass, setPopupClass] = useState("")
    const [bgOpacity, setBgOpacity] = useState("bg-opacity")

    const popup = (skill) => {

        if (popupVisible === "visible"){
            setPopupVisible("hidden")
        }
        else{
            setPopupVisible("visible")
        }

        setPopupClass(skill)
        console.log(popupClass , popupVisible)

    }

    return (
        <div className='white-bg-1'>
                <section data-aos="fade-up" className="skills-container">
                    <div className="titles">
                        <h1>Skills</h1>
                    </div>
                    <div className='skills'>
                    <p>Click on the skills below for more details!</p>
                        <div className='programming-languages'>
                            <a onClick= {() => {popup("c++")}}><SkillItem img={cppLogo}/></a>
                            <a onClick= {() => {popup("python")}}><SkillItem img="https://picsum.photos/200"/></a>
                        </div>
                        <div className={popupVisible}>
                            <SkillPopup skill={popupClass}/>
                        </div>
                    </div>
                </section>
        </div>
    );
}

export default Skills;
